import React from "react";
import { useNavigate } from "react-router-dom";
import { useFormik } from "formik";
// Chakra imports
import {
  Box,
  Button,
  Checkbox,
  Flex,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Heading,
  Icon,
  Input,
  InputGroup,
  InputRightElement,
  Text,
  useColorModeValue,
  useBreakpointValue,
} from "@chakra-ui/react";

// Custom components
import DefaultAuth from "layouts/auth/Default";
// Assets
import illustration from "assets/img/auth/PNG-02.png";

import { MdOutlineRemoveRedEye } from "react-icons/md";
import { RiEyeCloseLine } from "react-icons/ri";
import { postApi } from "services/api";
import { loginSchema } from "schema";
import { toast } from "react-toastify";
import Spinner from "components/spinner/Spinner";

function SignIn() {
  // Chakra color mode variables
  const textColor = useColorModeValue("navy.700", "white");
  const textColorSecondary = useColorModeValue("gray.500", "gray.400");
  const brandStars = useColorModeValue("brand.500", "brand.400");
  const [isLoading, setIsLoading] = React.useState(false);
  const [checkBox, setCheckBox] = React.useState(true);
  const [show, setShow] = React.useState(false);
  const showPass = () => setShow(!show);

  const initialValues = {
    username: "",
    password: "",
  };

  const navigate = useNavigate();

  const {
    errors,
    values,
    touched,
    handleBlur,
    handleChange,
    resetForm,
    handleSubmit,
  } = useFormik({
    initialValues: initialValues,
    validationSchema: loginSchema,
    onSubmit: (values, { resetForm }) => {
      login();
    },
  });

  const login = async () => {
    try {
      setIsLoading(true);
      let response = await postApi("api/user/login", values, checkBox);
      if (response && response.status === 200) {
        navigate("/admin");
        toast.success("Login Successfully!");
        resetForm();
      } else {
        toast.error(response.response.data?.error);
      }
    } catch (e) {
      console.log(e);
      toast.error("Something went wrong, please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  // Responsive width for the form container
  const formWidth = useBreakpointValue({ base: "100%", md: "420px" });

  return (
    <DefaultAuth illustrationBackground={illustration} image={illustration}>
      <Flex
        maxW={{ base: "100%", md: "max-content" }}
        w="100%"
        mx={{ base: "auto", lg: "0px" }}
        me="auto"
        h="fit-content"
        alignItems="start"
        justifyContent="center"
        mb={{ base: "30px", md: "60px" }}
        px={{ base: "25px", md: "0px" }}
        mt={{ base: "40px", md: "14vh" }}
        flexDirection="column"
        bg={useColorModeValue("whiteAlpha.800", "blackAlpha.600")} // subtle background for contrast
        p={8}
        rounded="lg"
        boxShadow="xl"
      >
        <Box me="auto">
          <Heading
            color={textColor}
            fontSize={{ base: "28px", md: "36px" }}
            fontWeight="bold"
            mb="10px"
          >
            Sign In
          </Heading>
          <Text
            mb="36px"
            ms="4px"
            color={textColorSecondary}
            fontWeight="400"
            fontSize={{ base: "md", md: "lg" }}
          >
            Enter your email and password to sign in!
          </Text>
        </Box>

        <Flex
          direction="column"
          w={formWidth}
          maxW="100%"
          background="transparent"
          borderRadius="15px"
          mx={{ base: "auto", lg: "unset" }}
          me="auto"
          mb={{ base: "20px", md: "auto" }}
        >
          <form onSubmit={handleSubmit}>
            <FormControl
              isInvalid={errors.username && touched.username}
              mb={errors.username && touched.username ? "2" : "6"}
            >
              <FormLabel
                display="flex"
                ms="4px"
                fontSize="sm"
                fontWeight="600"
                color={textColor}
                mb="8px"
              >
                Email<Text color={brandStars}>*</Text>
              </FormLabel>
              <Input
                fontSize="md"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.username}
                name="username"
                type="email"
                placeholder="mail@example.com"
                fontWeight="500"
                size="lg"
                borderColor={
                  errors.username && touched.username ? "red.400" : "gray.300"
                }
                focusBorderColor="brand.500"
                className={errors.username && touched.username ? "isInvalid" : ""}
              />
              {errors.username && touched.username && (
                <FormErrorMessage mt="1">{errors.username}</FormErrorMessage>
              )}
            </FormControl>

            <FormControl
              isInvalid={errors.password && touched.password}
              mb={errors.password && touched.password ? "2" : "6"}
            >
              <FormLabel
                ms="4px"
                fontSize="sm"
                fontWeight="600"
                color={textColor}
                display="flex"
              >
                Password<Text color={brandStars}>*</Text>
              </FormLabel>
              <InputGroup size="md">
                <Input
                  isRequired={true}
                  fontSize="md"
                  placeholder="Enter Your Password"
                  name="password"
                  value={values.password}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  size="lg"
                  type={show ? "text" : "password"}
                  borderColor={
                    errors.password && touched.password ? "red.400" : "gray.300"
                  }
                  focusBorderColor="brand.500"
                  className={
                    errors.password && touched.password ? "isInvalid" : ""
                  }
                />
                <InputRightElement display="flex" alignItems="center" mt="4px">
                  <Icon
                    color={textColorSecondary}
                    _hover={{ cursor: "pointer" }}
                    as={show ? RiEyeCloseLine : MdOutlineRemoveRedEye}
                    onClick={showPass}
                    aria-label={show ? "Hide password" : "Show password"}
                  />
                </InputRightElement>
              </InputGroup>
              {errors.password && touched.password && (
                <FormErrorMessage mt="1">{errors.password}</FormErrorMessage>
              )}
            </FormControl>

            <Flex justifyContent="start" align="center" mb="24px">
              <Checkbox
                onChange={(e) => setCheckBox(e.target.checked)}
                id="remember-login"
                isChecked={checkBox}
                colorScheme="brandScheme"
                me="10px"
              />
              <FormLabel
                htmlFor="remember-login"
                mb="0"
                fontWeight="normal"
                color={textColor}
                fontSize="sm"
                cursor="pointer"
              >
                Keep me logged in
              </FormLabel>
            </Flex>

            <Button
              fontSize="md"
              variant="brand"
              fontWeight="600"
              w="100%"
              h="50px"
              type="submit"
              mb="24px"
              isDisabled={isLoading}
              _hover={{ bg: "brand.600" }}
            >
              {isLoading ? <Spinner /> : "Sign In"}
            </Button>
          </form>
        </Flex>
      </Flex>
    </DefaultAuth>
  );
}

export default SignIn;
