import { CloseIcon, PhoneIcon, StarIcon } from '@chakra-ui/icons';
import { Box, Button, Drawer, DrawerBody, DrawerContent, DrawerFooter, DrawerHeader, DrawerOverlay, FormLabel, Grid, GridItem, Heading, IconButton, Input, InputGroup, InputLeftElement, Radio, RadioGroup, Select, Slider, SliderFilledTrack, SliderThumb, SliderTrack, Stack, Text } from '@chakra-ui/react';
import { HSeparator } from 'components/separator/Separator';
import Spinner from 'components/spinner/Spinner';
import { useFormik } from 'formik';
import { useState } from 'react';
import { BiMobile } from 'react-icons/bi';
import { contactSchema } from 'schema';
import { postApi } from 'services/api';

const Add = (props) => {
    const [isLoding, setIsLoding] = useState(false)

    const initialValues = {
        firstName: "",
        lastName: "",
        title: "",
        email: "",
        phoneNumber: "",
        mobileNumber: "",
        physicalAddress: "",
        mailingAddress: "",
        preferredContactMethod: "",
        // 2.Lead Source Information
        leadSource: "",
        referralSource: "",
        campaignSource: "",
        // 3. Status and Classifications
        leadStatus: "",
        leadRating: "",
        leadConversionProbability: "",
        // 5. History:
        notesandComments: "",
        // 6. Tags or Categories
        tagsOrLabelsForcategorizingcontacts: "",
        // 7. Important Dates::
        birthday: "",
        anniversary: "",
        keyMilestones: "",
        // 8. Additional Personal Information
        dob: "",
        gender: "",
        occupation: "",
        interestsOrHobbies: "",
        // 9. Preferred  Communication Preferences:
        communicationFrequency: "",
        preferences: "",
        // 10. Social Media Profiles:
        linkedInProfile: "",
        facebookProfile: "",
        twitterHandle: "",
        otherProfiles: "",
        // 11. Lead Assignment and Team Collaboration:
        agentOrTeamMember: "",
        internalNotesOrComments: "",
        createBy: JSON.parse(localStorage.getItem('user'))._id,
    };

    const formik = useFormik({
        initialValues: initialValues,
        validationSchema: contactSchema,
        onSubmit: (values, { resetForm }) => {
            AddData();
            resetForm();
        },
    });

    const { errors, touched, values, handleBlur, handleChange, handleSubmit, setFieldValue, } = formik

    const AddData = async () => {
        try {
            setIsLoding(true)
            let response = await postApi('api/contact/add', values)
            if (response.status === 200) {
                props.onClose();
            }
        } catch (e) {
            console.log(e);
        }
        finally {
            setIsLoding(false)
        }

    };

    const handleCancel = () => {
        formik.resetForm();
        props.onClose()
    }

    return (
        <div>
            <Drawer isOpen={props.isOpen} size={props.size}>
                <DrawerOverlay />
                <DrawerContent>
                    <DrawerHeader justifyContent='space-between' display='flex' >
                        Add Contacts
                        <IconButton onClick={props.onClose} icon={<CloseIcon />} />
                    </DrawerHeader>
                    <DrawerBody>

                        <Grid templateColumns="repeat(12, 1fr)" gap={3}>

                            <GridItem colSpan={{ base: 12 }}>
                                <Heading as="h1" size="md" >
                                    1. Basic Information
                                </Heading>
                            </GridItem>

                            <GridItem colSpan={{ base: 12, sm: 6 }}>
                                <FormLabel display='flex' ms='4px' fontSize='sm' fontWeight='500' mb='8px'>
                                    First Name<Text color={"red"}>*</Text>
                                </FormLabel>
                                <Input
                                    fontSize='sm'
                                    onChange={handleChange} onBlur={handleBlur}
                                    value={values.firstName}
                                    name="firstName"
                                    placeholder='Enter First Name'
                                    // mb={errors.firstName && touched.firstName ? undefined : '10px'}
                                    fontWeight='500'
                                    borderColor={errors.firstName && touched.firstName ? "red.300" : null}
                                />
                                <Text mb='10px' color={'red'}> {errors.firstName && touched.firstName && errors.firstName}</Text>
                            </GridItem>

                            <GridItem colSpan={{ base: 12, sm: 6 }}>
                                <FormLabel display='flex' ms='4px' fontSize='sm' fontWeight='500' mb='8px'>
                                    Last Name<Text color={"red"}>*</Text>
                                </FormLabel>
                                <Input
                                    fontSize='sm'
                                    onChange={handleChange} onBlur={handleBlur}
                                    value={values.lastName}
                                    name="lastName"
                                    placeholder='Enter Last Name'
                                    mb={errors.lastName && touched.lastName ? undefined : '10px'}
                                    fontWeight='500'
                                    borderColor={errors.lastName && touched.lastName ? "red.300" : null}
                                />
                                {errors.lastName && touched.lastName && <Text mb='10px' color={'red'}> {errors.lastName}</Text>}
                            </GridItem>

                            <GridItem colSpan={{ base: 12, sm: 6 }}>
                                <FormLabel display='flex' ms='4px' fontSize='sm' fontWeight='500' mb='8px'>
                                    Title<Text color={"red"}>*</Text>
                                </FormLabel>
                                <Input
                                    fontSize='sm'
                                    onChange={handleChange} onBlur={handleBlur}
