import React from "react";
import { useNavigate } from "react-router-dom";
import { useFormik } from "formik";
import {
  Box,
  Button,
  Checkbox,
  Flex,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Heading,
  Icon,
  Input,
  InputGroup,
  InputRightElement,
  Text,
  useColorModeValue,
  useBreakpointValue,
} from "@chakra-ui/react";

import DefaultAuth from "layouts/auth/Default";
import illustration from "assets/img/auth/PNG-02.png";

import { MdOutlineRemoveRedEye } from "react-icons/md";
import { RiEyeCloseLine } from "react-icons/ri";
import { postApi } from "services/api";
import { loginSchema } from "schema";
import { toast } from "react-toastify";
import Spinner from "components/spinner/Spinner";

function SignIn() {
  const textColor = useColorModeValue("gray.800", "white");
  const textColorSecondary = useColorModeValue("gray.600", "gray.300");
  const brandStars = useColorModeValue("brand.500", "brand.400");

  const [isLoading, setIsLoading] = React.useState(false);
  const [checkBox, setCheckBox] = React.useState(true);
  const [show, setShow] = React.useState(false);
  const showPass = () => setShow(!show);

  const initialValues = {
    username: "",
    password: "",
  };

  const navigate = useNavigate();

  const {
    errors,
    values,
    touched,
    handleBlur,
    handleChange,
    resetForm,
    handleSubmit,
  } = useFormik({
    initialValues,
    validationSchema: loginSchema,
    onSubmit: () => {
      login();
    },
  });

  const login = async () => {
    try {
      setIsLoading(true);
      let response = await postApi("api/user/login", values, checkBox);
      if (response && response.status === 200) {
        navigate("/admin");
        toast.success("Login Successfully!");
        resetForm();
      } else {
        toast.error(response.response.data?.error);
      }
    } catch (e) {
      console.error(e);
      toast.error("An unexpected error occurred.");
    } finally {
      setIsLoading(false);
    }
  };

  // Responsive width for the form container
  const formWidth = useBreakpointValue({ base: "90%", md: "400px", lg: "450px" });

  return (
    <DefaultAuth illustrationBackground={illustration} image={illustration}>
      {/* Overlay with dark blur to improve text readability */}
      <Box
        position="fixed"
        top={0}
        left={0}
        w="100vw"
        h="100vh"
        bg={useColorModeValue("rgba(255,255,255,0.8)", "rgba(0,0,0,0.6)")}
        backdropFilter="blur(6px)"
        zIndex={1}
      />

      <Flex
        position="relative"
        zIndex={2}
        minH="100vh"
        w="100%"
        justifyContent="center"
        alignItems="center"
        px={{ base: 4, md: 0 }}
      >
        <Box
          bg={useColorModeValue("white", "gray.800")}
          boxShadow="xl"
          borderRadius="lg"
          p={{ base: 8, md: 10 }}
          w={formWidth}
          zIndex={3}
          maxW="500px"
          textAlign="center"
        >
          <Heading
            fontSize={{ base: "3xl", md: "4xl" }}
            mb={6}
            color={textColor}
            fontWeight="extrabold"
            letterSpacing="tight"
          >
            Welcome Back
          </Heading>
          <Text
            mb={8}
            color={textColorSecondary}
            fontSize={{ base: "md", md: "lg" }}
            fontWeight="medium"
          >
            Sign in to your account with your email and password
          </Text>

          <form onSubmit={handleSubmit} noValidate>
            <FormControl isInvalid={errors.username && touched.username} mb={6}>
              <FormLabel
                fontWeight="semibold"
                color={textColor}
                htmlFor="username"
                mb={2}
              >
                Email Address <Text as="span" color={brandStars}>*</Text>
              </FormLabel>
              <Input
                id="username"
                name="username"
                type="email"
                placeholder="your.email@example.com"
                value={values.username}
                onChange={handleChange}
                onBlur={handleBlur}
                size="lg"
                borderColor={errors.username && touched.username ? "red.400" : "gray.300"}
                focusBorderColor="brand.500"
                autoComplete="username"
              />
              {errors.username && touched.username && (
                <FormErrorMessage>{errors.username}</FormErrorMessage>
              )}
            </FormControl>

            <FormControl isInvalid={errors.password && touched.password} mb={6}>
              <FormLabel
                fontWeight="semibold"
                color={textColor}
                htmlFor="password"
                mb={2}
              >
                Password <Text as="span" color={brandStars}>*</Text>
              </FormLabel>
              <InputGroup size="lg">
                <Input
                  id="password"
                  name="password"
                  type={show ? "text" : "password"}
                  placeholder="Enter your password"
                  value={values.password}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  borderColor={errors.password && touched.password ? "red.400" : "gray.300"}
                  focusBorderColor="brand.500"
                  autoComplete="current-password"
                />
                <InputRightElement>
                  <Icon
                    as={show ? RiEyeCloseLine : MdOutlineRemoveRedEye}
                    onClick={showPass}
                    cursor="pointer"
                    color={textColorSecondary}
                    boxSize={5}
                    aria-label={show ? "Hide password" : "Show password"}
                  />
                </InputRightElement>
              </InputGroup>
              {errors.password && touched.password && (
                <FormErrorMessage>{errors.password}</FormErrorMessage>
              )}
            </FormControl>

            <Flex
              justifyContent="space-between"
              alignItems="center"
              mb={8}
              fontSize="sm"
              color={textColorSecondary}
            >
              <Checkbox
                id="remember-login"
                isChecked={checkBox}
                onChange={(e) => setCheckBox(e.target.checked)}
                colorScheme="brandScheme"
              >
                Keep me logged in
              </Checkbox>
              {/* You can add forgot password here if needed */}
            </Flex>

            <Button
              type="submit"
              colorScheme="brandScheme"
              size="lg"
              fontWeight="bold"
              w="full"
              isLoading={isLoading}
              loadingText="Signing In"
              _hover={{ bg: "brand.600" }}
            >
              Sign In
            </Button>
          </form>
        </Box>
      </Flex>
    </DefaultAuth>
  );
}

export default SignIn;
